import React, { useState, useContext } from 'react';
import ButtonCart from  '../../components/ButtonCart/ButtonCart.js';
import { Formik } from "formik"; // Importamos el component <Formik />
import { CartContext } from '../../components/Context/CartContext.js';
import {getFirestore} from '../../firebase';
import * as firebase from 'firebase/app';
import "./formulario.css"
import 'firebase/firestore';
import {
  Container,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Card,
  CardBody,
  CardHeader,
  FormFeedback,
} from "reactstrap";

function Orden(props) {
  const [cart] = useContext(CartContext);
  const [inputs, setInputs] = useState({});


  const [orderId, setOrderID] = useState()
  const [total, setTotal] = useState({});

  const price = cart.map((item) => item.total);
  const sum = price.reduce((a, b) => a + b,0);


  const handleChange = e => {
    e.persist()
          setInputs(inputs => ({ ...inputs, [e.target.name]: e.target.value, [e.target.number]: e.target.value, [e.target.email]: e.target.value}))
  }


  function fire(e){
    e.preventDefault();
    const db = getFirestore();
    const orders = db.collection("orders");

    console.log(cart);
    orders.add(
        {
        buyer: inputs,
        items: cart,
        data: firebase.firestore.Timestamp.fromDate(new Date()),
        total: cart.reduce((a,c) => (a+c.product.precio*c.count), 0),
      }
      ).then(({id})=>{
        setOrderID(id)//sucess
        clearState()
      }).catch((error) =>{
        console.log('Error add orders: ', error);
     }).finally(() =>{
        console.log(orderId);
      });
    };




  function clearState(){
       setInputs('')

      }

      return (
  <Container className="p-5">
    <Card>
      <CardHeader></CardHeader>
      <CardBody>
        <Formik
          initialValues={{
            name: "",
            email: "",
            conEmail: "",
            telefono: "",
          }}
          validate={(values) => {
            const errors = {};

            // We need a name
            if (!values.name) errors.name = "Required";

            // We need a valid e-mail
            if (!values.email) errors.email = "Required";
            else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email))
              errors.email = "Invalid email address";


              // We need a valid e-mail
              if (!values.conEmail) errors.conEmail = "Required";
              else if (values.email = values.conEmail)
                errors.conEmail = "Los mails deben ser iguales";

              // We need a valid e-mail
              if (!values.telefono) errors.telefono = "Required";



            return errors;
          }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));

              setSubmitting(false);
            }, 250);
          }}
        >
          {(props) => {
            const {
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting,
              /* y otras más */
            } = props;
            return (
              <Form onSubmit={handleSubmit}>
                <h1>Form</h1>
                <FormGroup>
                  <Label for="name">Name</Label>
                  <Input
                    type="text"
                    name="name"
                    placeholder="Woody Allen"
                    invalid={errors.name && touched.name}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.name}
                  />
                  <FormFeedback>{errors.name}</FormFeedback>
                </FormGroup>
                <FormGroup>
                  <Label for="email">Email</Label>
                  <Input
                    type="email"
                    name="email"
                    placeholder="contoso@domain.com"
                    invalid={errors.email && touched.email}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.email}
                  />
                  <FormFeedback>{errors.email}</FormFeedback>
                </FormGroup>
                <FormGroup>
                  <Label for="email">Email</Label>
                  <Input
                    type="email"
                    name="conEmail"
                    placeholder="contoso@domain.com"
                    invalid={errors.conEmail && touched.conEmail}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.conEmail}
                  />
                  <FormFeedback>{errors.email}</FormFeedback>
                </FormGroup>
                <FormGroup>
                  <Label for="telefono">Telefono</Label>
                  <Input
                    type="number"
                    name="telefono"
                    placeholder="1565656565"
                    invalid={errors.telefono && touched.telefono}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.telefono}
                  />
                  <FormFeedback>{errors.telefono}</FormFeedback>
                </FormGroup>


                <Button type="submit" disabled={isSubmitting}>
                  {isSubmitting ? `Loading` : `Submit`}
                </Button>
              </Form>
            );
          }}
        </Formik>
      </CardBody>
    </Card>
  </Container>
);
}
    // if(cart.length === 0){
    //   return <div>
    //   <p className="tituloPrincipal">Carrito</p>
    //   <ButtonCart /></div>
    // } else{
    // return(<div>
    //       <p className="tituloOrden mt-3"> ¡Último paso! </p>
    //
    //         <Form className="formulario" style={{
    //
    //           }}>
    //           <FormGroup row>
    //               <Label for="exampleEmail" sm={12}>Email</Label>
    //               <Col sm={12}>
    //               <Input  onChange={handleChange} type="email" name="email" id="exampleEmail" placeholder="nombre@dominio.com" />
    //
    //               </Col>
    //
    //           </FormGroup>
    //           <FormGroup row>
    //               <Label for="exampleEmail" sm={12}>Confirmar Email</Label>
    //               <Col sm={12}>
    //               <Input onChange={handleChange} type="email" name="confirmEmail" id="confirmEmail" placeholder="nombre@dominio.com" />
    //               </Col>
    //           </FormGroup>
    //           <FormGroup row>
    //               <Label for="phone" sm={12}>Telefono</Label>
    //               <Col sm={12}>
    //               <Input onChange={handleChange} type="number" name="number" id="exampleNumber" placeholder="1165579988" />
    //               </Col>
    //           </FormGroup>
    //           <FormGroup row>
    //               <Label for="name" sm={12}>Nombre y Apellido</Label>
    //               <Col sm={12}>
    //               <Input onChange={handleChange} type="name" name="name" id="name" placeholder="" />
    //               </Col>
    //           </FormGroup>
    //           <FormGroup check row>
    //               <Col >
    //               <Button className="btn btn-primary botonOrden mt-3" onClick={(e)=>fire(e)}>Enviar Orden</Button>
    //               </Col>
    //           </FormGroup>
    //           {orderId ? <div>
    //           <h1 className="gracias mt-4"> ¡Gracias por realizar la compra! </h1>
    //           <p className="orden" >Orden: {orderId}</p>
    //           </div> : ''}
    //           </Form>
    //     </div>)
    // }
// }
export default Orden;
