import React from 'react';
import logo from './logo.svg';
import {BrowserRouter, Switch, Route} from 'react-router-dom';
import './App.css';
import Nav from './components/Nav/NavBar.js';
import Listado from './components/List/Listado.js';
import Cart from './components/Cart/Cart.js';

import Detail from './components/ItemDetailContainer/ItemDetailContainer.js';
import {CartProvider} from './components/Context/CartContext.js'





function App() {
  return (
    <BrowserRouter>
    <Nav />
    <Switch>
    <CartProvider>
    <Route exact path="/">
    <Listado />
    </Route>
    <Route  path="/detail/:id">
    <Detail />
    </Route>
    // <Route  path="/cart">
    // <Cart />
    // </Route>
    </CartProvider>

    </Switch>
    </BrowserRouter>


  );
}

export default App;
