import React, { useState, Component } from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


class FormularioOrden extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nombre: '',
      apellido: '',


    };

    const [datos, setDatos] = useState({
       nombre: '',
       apellido: ''
   })

    this.handleChangeDatos = this.handleChangeDatos.bind(this);

  }

  handleChangeDatos(event) {
    this.setState({name: event.target.value, apellido: event.target.value});

    console.log(event.target.value);
  }


  render() {
    return (

      <Form className="formulario mt-3">
        <FormGroup>
          <Label >Nombre</Label>
          <Input type="text" name="nombre" id="nombre" onChange={this.handleChangeDatos} />
        </FormGroup>
        <FormGroup>
          <Label >Apellido</Label>
          <Input type="text" name="nombre" id="nombre" onChange={this.handleChangeDatos} />
        </FormGroup>


      </Form>
    );
  }
}

export default FormularioOrden;
