import React, {useEffect, useState} from 'react';
import Formulario from "../../components/Formulario/Formulario.js";
import {NavLink} from 'react-router-dom';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';


import './product.css';


const Listado = (props) => {
      const [item, setItem] = useState([]);
      const [loading, setLoading] = useState(false);


useEffect(() =>{
  setLoading(true);
  fetch('https://api.mercadolibre.com/sites/MLA/search?q=Correas%20Perro&limit=5')
  .then(response =>{
    return response.json();
  })
  .then(res => {
    setItem(res.results);
    setLoading(false);
  })
}, [])

useEffect(() =>{
  console.log(item);
}, [item])


if(loading){
  return <div>Loading...</div>
}

return(

  <div>
  {item.map((item_data) => {
      return (<Card style={{'margin': '30px'}}>
         <CardBody>
           <CardTitle>{item_data.title}</CardTitle>
           <CardSubtitle>{item_data.ids}</CardSubtitle>
           <CardText></CardText>
           <Button>Ver</Button>
         </CardBody>
       </Card>);
  })};
  </div>
);
}





export default Listado;
