import React, {Component, UseState} from 'react';
import Boton from './Botones.js';

class Formulario extends Component{
  constructor(){
    super();
      const Restar = function Restar() {
      alert('hola');
     }

    const Sumar = function Sumar() {
      alert('hola');
    }
  }

  render(){
    return(
      <div>
        <Boton onClick={this.Restar} contenido={'-'}/>
         <input type="number" className="form-control" id='contador' value='0' />
        <Boton onClick={this.Sumar}contenido={'+'}/>
      </div>
    );
  }
}


export default Formulario;
