}

return(

  <div>
  <p className="tituloPrincipal">Nuestros Productos</p>
  <CardGroup className="mt-3"style={{'margin': '30px'}}>
  {item.map((item_data) => {
      return (
             <Card style={{'margin': '10px'}} key={item_data.id}>
               <CardImg top width="100%" src="https://quierotodo.com.ar/1413-thickbox_default/correa-camuflado-fucsia-25mm.jpg" alt="Card image cap" />
               <CardBody>
                 <CardTitle className='tituloList'>{item_data.nombre}</CardTitle>
                 <CardSubtitle  className='codigoList'  >Codigo : {item_data.id}</CardSubtitle>
                  <NavLink to={`/detail/${item_data.id}`} style={{'color': 'white'}}>
                 <Button  className='mt-3 btn btn-block'>Ver</Button>
                 </NavLink>
               </CardBody>
             </Card>)
  })}
  </CardGroup>
  </div>
);
}





export default Listado;
