import React, { UseContext } from 'react';
import { CartContext } from '../../components/Context/CartContext.js';
import ButtonCart from  '../../components/ButtonCart/ButtonCart.js';
import DetalleCarrito from  '../../components/ButtonCart/ButtonCart.js';

import './cart.css';
import {
  Card, Button, CardImg, CardTitle, CardText, CardGroup,
  CardSubtitle, CardBody, NavItem
} from 'reactstrap';
import { NavLink as RRNavLink } from 'react-router-dom';// ojo Agrega esta linea para que funcione react-router-dom
import { Link } from 'react-router-dom';
import { NavLink } from 'react-router-dom';


const Cart = () => {
  const [cart] = React.useContext(CartContext);
  const productsInCart = cart.length > 0;
  console.log(cart);
  return (
    <div >

    <p className="tituloPrincipal">Carrito</p>
    {!productsInCart && <ButtonCart />}
    {productsInCart  && <DetalleCarrito />}
      {cart.map((item_data) => {
          return (
                 <Card className="imgCard" style={{'margin': '30px'}} key={item_data.id}>
                   <CardBody>
                     <CardTitle className='tituloList'>{item_data.nombre}</CardTitle>
                     <CardSubtitle  className='codigoList'  >${item_data.precio}</CardSubtitle>
                   </CardBody>
                   <NavItem>
                   <NavLink tag={RRNavLink} exact to="/cart" activeClassName="active" className="itemnav">
                      Finalizar
                     </NavLink>
                   </NavItem>
                 </Card>)
      })},

    }

    </div>
  )
}

export default Cart;
