import React, { UseContext } from 'react';
import { CartContext } from '../../components/Context/CartContext.js';


const Cart = () => {
  const [cart] = React.useContext(CartContext);
  console.log(cart);
  return (
    <div >
      <h2 >Cart</h2>
      <span>Items in cart: {cart.length}</span>
      <br></br>
      <span>Total Price: 0</span>
    </div>
  )
}

export default Cart;
