import React, {Component, UseState} from 'react';
import Boton from './Botones.js';
import './formulario.css';

class Formulario extends Component{
  constructor(props){
    super(props);

      /*1) Aquí declaramos las funciones creadas
        más abajo como funciones pertenencientes
        a la clase. Un bodrio, ¿no?
        Otro punto para componentes funcionales y hook.
        */
      this.Restar = this.Restar.bind(this)
      this.Sumar = this.Sumar.bind(this)
      this.state = {
      counter: 0,
    }
  }

  Restar() {
     let counter = this.state.counter -1;
      this.setState({counter});
  }

  Sumar() {
    let counter = this.state.counter +1;
    this.setState({counter});

  }

  render(){
    return(
      <div >
      <div className="d-flex justify-content-center estilodiv">
        <Boton onClick={this.Restar} contenido={'-'}/>
         <input type="number" className="form-control inputcontador" id='contador' value={this.state.counter} style={{'width': '35%'}}  />
        <Boton onClick={this.Sumar}contenido={'+'}/>
        </div>

      </div>
    );
  }

}

export default Formulario;
