import React, { UseState, useEffect } from 'react';



export const CartContext = React.createContext([]);
export const CartProvider = (props) => {
  const [cart, setCart] = ([]);

  useEffect(() => {
    console.log('desde context', cart);
  },[cart])

function Carter(a, b){
  console.log(a, b);
  setCart({a: b})
}

  return(
    <CartContext.Provider value={[Carter]}>
    {props.children}
    </CartContext.Provider>
  )
}
