import React, {useEffect, useState} from 'react';
import Formulario from "../../components/Formulario/Formulario.js";


function ItemDetail(props) {
return(

  <div>
  <div className="row mt-4"  style={{'padding': '40px'}}>
  <div className="col-6 divimagen" >
  <img src="https://quierotodo.com.ar/1413-thickbox_default/correa-camuflado-fucsia-25mm.jpg" width="70%" className="d-flex" style={{'margin': 'auto'}}/>

  </div>
  <div className="col-6">
  <h4 className="titulo mt-3">{props.value.title}</h4>
  <p className="codigo"> Codigo: {props.value.id}</p>
  <p className="precio" style={{'font-size':' 35px'}}>${props.value.price}</p>
  <Formulario/>
  <a className="btn btn-primary boton mt-3" href={props.value.permalink}> Comprar</a>
  </div>

  </div>



  </div>
);
}

export default ItemDetail;
