import React, {useEffect, useState, useContext} from 'react';
import { getFirestore } from '../../firebase';
import { CartContext } from '../../components/Context/CartContext.js';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


const Orden = () => {
  const db = getFirestore();
  const orders = db.collection("orders");
  const [loading, setLoading] = useState(false);
  const [order, setOrderId] = useState();
  const [error, setError] = useState();
  const [cart] = React.useContext(CartContext);



  const newOrder = {
      buyer: 'userInfo',
      items : cart,
  };

  // Set the Cart Context with the added product
  const enviar = () => {
    orders.add(newOrder).then(({id}) =>{
      setOrderId(id);
    }).catch(err => {
      setError(err);
    }).finally(() =>{
      setLoading(false);
    })  }


  return (
      <div>
      import React from 'react';


    <Form>
      <FormGroup>
        <Label for="exampleEmail">Email</Label>
        <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
      </FormGroup>
      <FormGroup>
        <Label for="examplePassword">Password</Label>
        <Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
      </FormGroup>
      <FormGroup>
        <Label for="exampleSelect">Select</Label>
        <Input type="select" name="select" id="exampleSelect">
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </Input>
      </FormGroup>
      <FormGroup>
        <Label for="exampleSelectMulti">Select Multiple</Label>
        <Input type="select" name="selectMulti" id="exampleSelectMulti" multiple>
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </Input>
      </FormGroup>
      <FormGroup>
        <Label for="exampleText">Text Area</Label>
        <Input type="textarea" name="text" id="exampleText" />
      </FormGroup>
      <FormGroup>
        <Label for="exampleFile">File</Label>
        <Input type="file" name="file" id="exampleFile" />
        <FormText color="muted">
          This is some placeholder block-level help text for the above input.
          It's a bit lighter and easily wraps to a new line.
        </FormText>
      </FormGroup>
      <FormGroup tag="fieldset">
        <legend>Radio Buttons</legend>
        <FormGroup check>
          <Label check>
            <Input type="radio" name="radio1" />{' '}
            Option one is this and thatâ€”be sure to include why it's great
          </Label>
        </FormGroup>
        <FormGroup check>
          <Label check>
            <Input type="radio" name="radio1" />{' '}
            Option two can be something else and selecting it will deselect option one
          </Label>
        </FormGroup>
        <FormGroup check disabled>
          <Label check>
            <Input type="radio" name="radio1" disabled />{' '}
            Option three is disabled
          </Label>
        </FormGroup>
      </FormGroup>
      <FormGroup check>
        <Label check>
          <Input type="checkbox" />{' '}
          Check me out
        </Label>
      </FormGroup>
      <Button>Submit</Button>
    </Form>
  );


      <button className="btn btn-primary boton mt-3" onClick={enviar}> Enviar</button>
      </div>
  );
}

export default Orden;
