import React, {UseContext} from 'react';
import { CartContext} from '../../components/Context/CartContext.js';

const Cart = () =>{
  const [cart, setCart] = UseContext(CartContext);

  return(
    <div>
     <h2>Carrito</h2>
     <span> Item en carrito:{cart.length} </span>
    </div>
  )
}

export default Cart;
