import React, { useState, useContext } from 'react';
import { Col, Button, Form, FormGroup, Label, Input} from 'reactstrap';
import ButtonCart from  '../../components/ButtonCart/ButtonCart.js';
import { Formik } from "formik"; // Importamos el component <Formik />

import { CartContext } from '../../components/Context/CartContext.js';
import {getFirestore} from '../../firebase';
import * as firebase from 'firebase/app';
import "./formulario.css"

import 'firebase/firestore';
function Orden(props) {
  const [cart] = useContext(CartContext);
  const [inputs, setInputs] = useState({});


  const [orderId, setOrderID] = useState()
  const [total, setTotal] = useState({});

  const price = cart.map((item) => item.total);
  const sum = price.reduce((a, b) => a + b,0);


  const handleChange = e => {
    e.persist()
          setInputs(inputs => ({ ...inputs, [e.target.name]: e.target.value, [e.target.number]: e.target.value, [e.target.email]: e.target.value}))
  }


  function fire(e){
    e.preventDefault();
    const db = getFirestore();
    const orders = db.collection("orders");

    console.log(cart);
    orders.add(
        {
        buyer: inputs,
        items: cart,
        data: firebase.firestore.Timestamp.fromDate(new Date()),
        total: cart.reduce((a,c) => (a+c.product.precio*c.count), 0),
      }
      ).then(({id})=>{
        setOrderID(id)//sucess
        clearState()
      }).catch((error) =>{
        console.log('Error add orders: ', error);
     }).finally(() =>{
        console.log(orderId);
      });
    };




  function clearState(){
       setInputs('')

      }
    // if(cart.length === 0){
    //   return <div>
    //   <p className="tituloPrincipal">Carrito</p>
    //   <ButtonCart /></div>
    // } else{
    return(<div>
          <p className="tituloOrden mt-3"> ¡Último paso! </p>

            <Form className="formulario" style={{

              }}>
              <FormGroup row>
                  <Label for="exampleEmail" sm={12}>Email</Label>
                  <Col sm={12}>
                  <Input  onChange={handleChange} type="email" name="email" id="exampleEmail" placeholder="nombre@dominio.com" />

                  </Col>

              </FormGroup>
              <FormGroup row>
                  <Label for="exampleEmail" sm={12}>Confirmar Email</Label>
                  <Col sm={12}>
                  <Input onChange={handleChange} type="email" name="confirmEmail" id="confirmEmail" placeholder="nombre@dominio.com" />
                  </Col>
              </FormGroup>
              <FormGroup row>
                  <Label for="phone" sm={12}>Telefono</Label>
                  <Col sm={12}>
                  <Input onChange={handleChange} type="number" name="number" id="exampleNumber" placeholder="1165579988" />
                  </Col>
              </FormGroup>
              <FormGroup row>
                  <Label for="name" sm={12}>Nombre y Apellido</Label>
                  <Col sm={12}>
                  <Input onChange={handleChange} type="name" name="name" id="name" placeholder="" />
                  </Col>
              </FormGroup>
              <FormGroup check row>
                  <Col >
                  <Button className="btn btn-primary botonOrden mt-3" onClick={(e)=>fire(e)}>Enviar Orden</Button>
                  </Col>
              </FormGroup>
              {orderId ? <div>
              <h1 className="gracias mt-4"> ¡Gracias por realizar la compra! </h1>
              <p className="orden" >Orden: {orderId}</p>
              </div> : ''}
              </Form>
        </div>)
    // }
}
export default Orden;
