import React, {useEffect, useState} from 'react';
import Formulario from "../../components/Formulario/Formulario.js";
import {NavLink} from 'react-router-dom';
import {
  Card, Button, CardImg, CardTitle, CardText, CardGroup,
  CardSubtitle, CardBody
} from 'reactstrap';



import './list.css';


const Listado = (props) => {
      const [item, setItem] = useState([]);
      const [loading, setLoading] = useState(false);


useEffect(() =>{
  setLoading(true);
  fetch('https://api.mercadolibre.com/sites/MLA/search?q=Correas%20Perro&limit=5')
  .then(response =>{
    return response.json();
  })
  .then(res => {
    setItem(res.results);
    setLoading(false);
  })
}, [])

useEffect(() =>{
  console.log(item);
}, [item])


if(loading){
  return <div>Loading...</div>
}

return(

  <div>
  <CardGroup style={{'margin': '30px'}}>

  {item.map((item_data) => {
      return (
             <Card style={{'margin': '10px'}}>
               <CardImg top width="100%" src="https://quierotodo.com.ar/1413-thickbox_default/correa-camuflado-fucsia-25mm.jpg" alt="Card image cap" />
               <CardBody>
                 <CardTitle className='tituloList'>{item_data.title}</CardTitle>
                 <CardSubtitle  className='codigoList'  >{item_data.id}</CardSubtitle>
                  <NavLink to={'/detail'} style={{'color': 'white'}}>
                 <Button  className='mt-3 btn btn-block'>Ver</Button>
                 </NavLink>
               </CardBody>
             </Card>)
  })};
  </CardGroup>
  </div>
);
}





export default Listado;
