import React, {useEffect, useState, useContext} from 'react';
import { useParams } from 'react-router-dom';
import { getFirestore } from '../../firebase';
import Loader from '../../components/loader/Loader';
import ItemCount from "../../components/ItemCount/ItemCount.js";

import { NavLink } from 'react-router-dom';
import { CartContext } from '../../components/Context/CartContext.js';

import './product.css';


function Product({ data }) {
  const { id } = useParams();
  const [size, setSize] = useState('');
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(false);

  const [cart, setCart] = useContext(CartContext);

  function giveMeCount(c){
    setCount(c)
  }


  useEffect(() => {
    setLoading(true)
    const db = getFirestore()
    const itemCollection = db.collection('items');
    const item = itemCollection.doc(id);

    item.get()
    .then((doc) => {
      if (!doc.exists) {
        console.log("Item does not exist!");
        return true;
      }
      const dataQuery = doc.data();
      console.log(dataQuery);
      setProduct({ id: doc.id, ...doc.data() });
    })
    .catch((error) => {
      console.log('Error searching item: ', error);
    })
    .finally(() => {
      setLoading(false);
    })
  }, [id]);

  useEffect(() => {
    console.log(size)
  }, [size]);


  const renderProduct = () => (

    <p className='Product-Description'>
      {product.description}
    </p>
  )

  // Set the Cart Context with the added product
  const addToCart = () => {
    setCart(currentCart => [...currentCart, product])
  }

  useEffect(() => {
    console.log(cart);
  }, [cart]);

  return(
    <div>
      {loading && <Loader />}
      {!loading &&
        <div>
        <div className="row mt-4"  style={{'padding': '40px'}}>
        <div className="col-6 divimagen" >
        <img src="https://quierotodo.com.ar/1413-thickbox_default/correa-camuflado-fucsia-25mm.jpg" width="70%" className="d-flex" style={{'margin': 'auto'}}/>

        </div>
        <div className="col-6">
        <h4 className="titulo mt-3">{props.value.nombre}</h4>
        <p className="codigo"> Codigo: { id }</p>
        <p className="precio" style={{'font-size':' 35px'}}>${props.value.precio}</p>
        <a className="btn btn-primary boton mt-3"> Comprar {count}</a>
        <button onClick={addToCart}>Agregar</button>
        </div>

        </div>



        </div>
      }
    </div>
  );
}

export default Product;
