import React, {useEffect, useState, useContext} from 'react';
import { getFirestore } from '../../firebase';
import { CartContext } from '../../components/Context/CartContext.js';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import "./formulario.css"


const Orden = () => {
  const db = getFirestore();
  const orders = db.collection("orders");
  const [loading, setLoading] = useState(false);
  const [order, setOrderId] = useState();
  const [error, setError] = useState();
  const [cart] = React.useContext(CartContext);



  const newOrder = {
      buyer: 'userInfo',
      items : cart,
  };

  // Set the Cart Context with the added product
  const enviar = () => {
    orders.add(newOrder).then(({id}) =>{
      setOrderId(id);
    }).catch(err => {
      setError(err);
    }).finally(() =>{
      setLoading(false);
    })  }


  return (
      <div>
      <p className="tituloOrden mt-3"> ¡Último paso! </p>
    <Form className="formulario mt-3">
      <FormGroup>
        <Label >Nombre</Label>
        <Input type="text" name="nombre" id="nombre" />
      </FormGroup>
      <FormGroup>
        <Label >Apellido</Label>
        <Input type="txt" name="apellido" id="apellido"  />
      </FormGroup>
      <FormGroup>
        <Label >Mail</Label>
        <Input type="email" name="email" id="email"  />
      </FormGroup>
    </Form>



      <button className="btn btn-primary botonOrden mt-3" onClick={enviar}> Enviar</button>
      </div>
  );
}

export default Orden;
