import React, { useState } from 'react';

export const CartContext = React.createContext([]);

export const CartProvider = (props) => {
  const [cart, setCart] = useState([]);
  const [long, setLong] = useState(0); //para manejar la longitud (cantidad) de items agregados al carrito (comienza en 0)
  const [item, setItem] = useState([]);

  const sumaProductos = (id) => {
    console.log(id);
  }



  return (
    <CartContext.Provider value={[cart, setCart, item, setItem, sumaProductos, long, setLong]}>
      {props.children}
    </CartContext.Provider>
  )
}
