import React, {useEffect, useState} from 'react';
import Formulario from "../../components/Formulario/Formulario.js";
import {NavLink} from 'react-router-dom';
import {
  Card, Button, CardImg, CardTitle, CardText, CardGroup,
  CardSubtitle, CardBody
} from 'reactstrap';



import './list.css';


const Listado = (props) => {
      const [item, setItem] = useState([]);
      const [loading, setLoading] = useState(false);


      useEffect(()=> {
        setTimeout(
          function(){
            fetch('https://5f3c95f36c11f80016d6f21e.mockapi.io/bitbuyer/items')
            .then(function(response) {
                return response.json();
            })
            .then(function(data){
                setItem(data)
            })
          }, 3000
        )
      },[])

useEffect(() =>{
  console.log(item);
}, [item])


if(loading){
  return <div>Loading...</div>
}

return(

  <div>
  <p className="tituloPrincipal">Nuestros Productos</p>
  <CardGroup className="mt-3"style={{'margin': '30px'}}>
  {item.map((item_data) => {
      return (
             <Card style={{'margin': '10px'}}>
               <CardImg top width="100%" src="https://quierotodo.com.ar/1413-thickbox_default/correa-camuflado-fucsia-25mm.jpg" alt="Card image cap" />
               <CardBody>
                 <CardTitle className='tituloList'>{item_data.nombre}</CardTitle>
                 <CardSubtitle  className='codigoList'  >Codigo : {item_data.id}</CardSubtitle>
                  <NavLink to={'/detail'} style={{'color': 'white'}}>
                 <Button  className='mt-3 btn btn-block'>Ver</Button>
                 </NavLink>
               </CardBody>
             </Card>)
  })};
  </CardGroup>
  </div>
);
}





export default Listado;
