import React, {useEffect, useState, Component} from 'react';
import BotonComprar from './Botones.js';

import './product.css';

class Detail extends Component {
  constructor(props) {
    super(props);
    this.comprar = this.comprar.bind(this)
    this.Detail = this.Detalle.bind(this)
  }
}

function Comprar(){
  alert('Comprar');
}
function Detalle(props) {
      const [item, setItem] = useState([]);
      const [loading, setLoading] = useState(false);


useEffect(() =>{
  setLoading(true);
  fetch('https://api.mercadolibre.com/items/MLA847761902')
  .then(response =>{
    return response.json();
  })
  .then(res => {
    setItem(res);
    setLoading(false);
  })
}, [])

useEffect(() =>{
  console.log(item);
}, [item])


if(loading){
  return <div>Loading...</div>
}

return(

  <div>
  <h4 className="titulo">{item.title}</h4>
  <img scr={item.secure_thumbnail} style={{'width':' 100px'}} />
  <BotonComprar onClick={this.Comprar} contenido={'-'}/>
  </div>
);
}

export default Detail;
