import React, {useEffect, useState} from 'react';
import ItemDetail from "../../components/ItemDetail/ItemDetail.js";
import {NavLink} from 'react-router-dom';
import './product.css';


const Detail= (props)=> {

    const [item, setItem] = useState(false);

    useEffect(() => {
        let id = props.match.params.id;
        console.log(props);
        setTimeout(
            function(){
                fetch(
                    `https://5f3c95f36c11f80016d6f21e.mockapi.io/bitbuyer/items/${id}`
                )
                .then(function(response){
                    return response.json();
                })
                .then(function(data){
                    setItem(data)
                })
            }, 3000
        )
    }, [])

    useEffect(()=>{
      console.log(item)
    }, [item])

    //Lo que se mapearía aquí es el componente ItemDetail,
    //  que mapea a a su vez a cada item.

    if(item === false){
        return <div style={{margin: '4vh 7vw'}}>
            Cargando item desde base de datos...
        </div>
    }else{
        return <div style={{margin: '4vh' }}>
            <h4>{item.nombre}</h4>
            <p>Precio {item.precio}</p>
            <p>Categoria: {item.categoria} </p>
            <hr></hr>
        </div>
    }

}

export default Detail;
