import React, { useState, Component } from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


class FormularioOrden extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nombre: '',
      apellido: '',
      email: '',
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({name: event.target.value});
  }

  render() {
    return (

      <Form className="formulario mt-3">
        <FormGroup>
          <Label >Nombre</Label>
          <Input type="text" name="nombre" id="nombre" onChange={this.handleChange} />
        </FormGroup>
        <FormGroup>
          <Label >Apellido</Label>
          <Input type="txt" name="apellido" id="apellido" onChange={this.handleChange}  />
        </FormGroup>
        <FormGroup>
          <Label >Mail</Label>
          <Input type="email" name="email" id="email"  onChange={this.handleChange}/>
        </FormGroup>
      </Form>
    );
  }
}

export default FormularioOrden;
