import React, { useState } from 'react';

export const CartContext = React.createContext([]);

export const CartProvider = (props) => {
  const [cart, setCart] = useState([]);
  const [long, setLong] = useState(0);

  const [item, setItem] = useState([]);

  const sumaProductos = (id) => {
    console.log(id);
  }

  function eraser(){
    setLong(0) //vuelvo longitud de items a 0 después de finalizar la compra
    setCart([]) //vacío el carrito después de ejectura la compra
}

  return (
    <CartContext.Provider value={[cart, setCart, item, setItem, sumaProductos]}>
      {props.children}
    </CartContext.Provider>
  )
}
