import React, {useEffect, useState, useContext} from 'react';
import { getFirestore } from '../../firebase';
import FormularioOrden from '../../components/FormularioOrden/FormularioOrden.js';
import { CartContext } from '../../components/Context/CartContext.js';
import "./formulario.css"


const Orden = (props) => {
  const db = getFirestore();
  const orders = db.collection("orders");
  const [loading, setLoading] = useState(false);
  const [order, setOrderId] = useState();
  const [error, setError] = useState();
  const [cart] = React.useContext(CartContext);
  const [inputs, setInputs] = useState({});


  const handleChange = e => {
          // Con esto accederemos a las propiedades del evento de forma asíncrona
          e.persist()
          // Ahora llamamos a la función, le pasamos el estado actual y le pasamos también el nombre del campo y su valor. Lo guardará justamente como un objeto
          setInputs(inputs => ({ ...inputs, [e.target.name]: e.target.value, [e.target.apellido]: e.target.value }))
      }


  const newOrder = {
      buyer: 'usuario',
      items : cart,
  };

  // Set the Cart Context with the added product
  const enviar = () => {
    orders.add(newOrder).then(({id}) =>{
      setOrderId(id);
    }).catch(err => {
      setError(err);
    }).finally(() =>{
      setLoading(false);
    })  }


  return (
      <div>
      <p className="tituloOrden mt-3"> ¡Último paso! </p>
      <FormularioOrden value={props}/>
      <button className="btn btn-primary botonOrden mt-3" onClick={enviar}> Enviar</button>
      </div>
  );
}

export default Orden;
