import React, {useEffect, useState, useContext} from 'react';
import { useParams } from 'react-router-dom';
import { getFirestore } from '../../firebase';
import Loader from '../../components/loader/Loader';
import { NavLink } from 'react-router-dom';
import { CartContext } from '../../components/Context/CartContext.js';
import ItemCount from "../../components/ItemCount/ItemCount.js";



import './product.css';


function Producto() {
  const [count, setCount] = useState(0);

  const { id } = useParams();
  const [size, setSize] = useState('');
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(false);
  const [cart, setCart, carter, long, setLong] = React.useContext(CartContext);

  function giveMeCount(c){
    setCount(c)
  }



  useEffect(() => {
    setLoading(true)
    const db = getFirestore()
    const itemCollection = db.collection('items');
    const item = itemCollection.doc(id);

    item.get()
    .then((doc) => {
      if (!doc.exists) {
        console.log("Item does not exist!");
        return true;
      }
      const dataQuery = doc.data();
      console.log(dataQuery);
      setProduct({ id: doc.id, ...doc.data() });
    })
    .catch((error) => {
      console.log('Error searching item: ', error);
    })
    .finally(() => {
      setLoading(false);
    })
  }, [id]);

  useEffect(() => {
    console.log(size)
  }, [size]);




  // Set the Cart Context with the added product
  const addToCart = () => {
    if(count === 0){
        alert('Seleccione una cantidad de ' + product.nombre)
    }else{
      setCart(currentCart => [...currentCart, {product, count}]);
      setLong(long + count)  //sumo a long (cantindad de items) la cantidad ingresada en la acción de compras
        var flag = false; //para marcar si tengo o no ya el item en el carrito
        var subtotal = product.precio * count; //subtotal => cantidad de item * precio
        setTotal(total + subtotal) //total => suma de todos los subtotales
        var kart = ([...cart]) //carrito que armao del state-cart

        if(kart.length > 0){ //si ya tengo un item en el carrito, examino si el que acabo de comprar tmb está
            for (let k in kart) {
                if(kart[k].id == product.id){ //si es que ya está, actualizo cantidad y precio total
                    kart[k].cantidad = kart[k].cantidad + count;
                    kart[k].subtotal = kart[k].subtotal + subtotal;
                    setCart(kart) //agrego carrito actualizado
                    flag = true //marco bandera
                }
            }
        }
        if(flag == false){ //si la bandera es true, entonces el item es nuevo y lo agrego por acá
            var purchase = {'cantidad': count,'nombre': product.nombre, 'id': product.id, 'subtotal': subtotal} //objeto de nueva compra
            var purchases = ([...cart, purchase]) //carrito con todo lo anterior y nueva compra
            setCart(purchases)
        }
    }
  }

  useEffect(() => {
    console.log(cart);
  }, [cart]);


  return(
    <>
      {loading && <Loader />}
      {!loading &&
        <div className="Product" style={{"display": "flex", "justifyContent": "center"}}>
        <div className="row mt-4"  style={{'padding': '40px'}}>
        <div className="col-6 divimagen" >
        <img src={product.img} width="70%" className="d-flex" style={{'margin': 'auto'}}/>
        </div>
        <div className="col-6">
        <h4 className="titulo mt-3">{product.nombre}</h4>
        <hr />
        <p className="codigo"> {product.descripcion}</p>
        <p className="precio" >${product.precio}</p>
        <ItemCount func={giveMeCount}/>
        <button className="btn btn-primary boton mt-3" onClick={addToCart}> Agregar {count}</button>
        </div>

        </div>
        </div>
      }
    </>
  );

}

export default Producto;
