import React, {useEffect, useState} from 'react';
import Formulario from "../../components/Formulario/Formulario.js";
import {NavLink} from 'react-router-dom';


import './product.css';


function Detail(props) {
      const [item, setItem] = useState([]);
      const [loading, setLoading] = useState(false);


useEffect(() =>{
  setLoading(true);
  fetch('https://api.mercadolibre.com/items/MLA847761902')
  .then(response =>{
    return response.json();
  })
  .then(res => {
    setItem(res);
    setLoading(false);
  })
}, [])

useEffect(() =>{
  console.log(item);
}, [item])


if(loading){
  return <div>Loading...</div>
}

return(

  <div>
  <div className="row mt-4">
  <div className="col-6 divimagen" style={{'border': '1px solid gray'}}>
  <img scr={item.secure_thumbnail} style={{'width':' 100px'}} />

  </div>
  <div className="col-6">
  <h4 className="titulo mt-3">{item.title}</h4>
  <p className="codigo"> Codigo: {item.id}</p>
  <p className="precio" style={{'font-size':' 35px'}}>${item.price}</p>
  <Formulario/>
  <a className="btn btn-primary boton mt-3" href={item.permalink}> Comprar</a>
  </div>

  </div>



  </div>
);
}

export default Detail;
