import React, {useEffect, useState} from 'react';
import { getFirestore } from '../../firebase';
import Loader from '../../components/loader/Loader';

import {NavLink} from 'react-router-dom';
import {
  Card, Button, CardImg, CardTitle, CardText, CardGroup,
  CardSubtitle, CardBody
} from 'reactstrap';




import './list.css';


const Listado = (props) => {
      const [items, setItems] = useState([]);
      const [loading, setLoading] = useState(false);


      useEffect(() => {
      setLoading(true);
      const db = getFirestore();
      const itemCollection = db.collection("items")

      itemCollection.get()
      .then((querySnapshot) => {
        if (querySnapshot.size === 0) {
          console.log('No data!');
        }
        setItems(querySnapshot.docs.map(doc => {
          return ({ id: doc.id, ...doc.data() });
        }));
      })
      .catch((error) => {
        console.log("There was an error trying to get items: ", error);
      })
      .finally(() => {
        setLoading(false);
      })
    }, []);

useEffect(() =>{
  console.log(items);
}, [items])



if (!loading) {
  return (
      <div>
      <p className="tituloPrincipal mt-3">NUESTROS PRODUCTOS</p>
      <CardGroup className="mt-3"style={{'margin': '30px'}}>
      {items.map((item_data) => {
          return (
                 <Card style={{'margin': '10px'}} key={item_data.id}>
                   <CardImg top width="100%" src="https://quierotodo.com.ar/1413-thickbox_default/correa-camuflado-fucsia-25mm.jpg" alt="Card image cap" />
                   <CardBody>
                     <CardTitle className='tituloList'>{item_data.nombre}</CardTitle>
                     <CardSubtitle  className='codigoList'  >Codigo : {item_data.id}</CardSubtitle>
                      <NavLink to={`/detail/${item_data.id}`} style={{'color': 'white'}}>
                     <Button  className='mt-3 btn btn-block'>Ver</Button>
                     </NavLink>
                   </CardBody>
                 </Card>)
      })}
      </CardGroup>
      </div>
  );
}
if (loading) {
  return (
    <div className="Home">
      <Loader />
    </div>
  );
}
}





export default Listado;
